import React, { useState, useEffect } from 'react';
import Filter from './Filter';

const url = 'https://restcountries.com/v3.1/all';

const Countries = () => {
  const [countries, setCountries] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // state to hold search query
  const [selectedRegion, setSelectedRegion] = useState(''); // state to hold selected region

  // Fetch data from REST Countries API
  const fetchCountryData = async () => {
    const response = await fetch(url);
    const countries = await response.json();
    setCountries(countries);
    console.log(countries);
  };

  useEffect(() => {
    fetchCountryData();
  }, []);

  // Filter countries based on the search query and selected region
  const filteredCountries = countries.filter((country) =>
    (country.name.common.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (country.capital && country.capital[0].toLowerCase().includes(searchQuery.toLowerCase()))) &&
    (selectedRegion === '' || country.region === selectedRegion)
  );

  return (
    <>
      {/* Pass selectedRegion and setSelectedRegion to Filter component */}
      <Filter
        selectedRegion={selectedRegion}
        setSelectedRegion={setSelectedRegion}
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
      />

      <section className='grid'>
        {filteredCountries.map((country) => {
          const { name, population, region, capital, flags } = country;

          return (
            <article key={name.common}>
              <div>
                <img src={flags.png} alt={name.common} />
                <div className='details'>
                  <h3>{name.common}</h3>
                  <h4>Population: <span>{population}</span></h4>
                  <h4>Region: <span>{region}</span></h4>
                  <h4>Capital: <span>{capital}</span></h4>
                </div>
              </div>
            </article>
          );
        })}
      </section>
    </>
  );
}

export default Countries;
